mapper.update:
UPDATE UserInfo
SET Nickname = #{nickname}, Gender = #{gender}, Birthday = #{birthday}, Location = #{location}, Bio = #{bio}
WHERE UserUid = #{useruid}

mapper.sql:
SELECT * FROM ( SELECT u.*, ROW_NUMBER() OVER(ORDER BY UserUid) AS rownum FROM UserInfo u WHERE 1=1
AND Nickname LIKE '%' + #{userInfo.nickname} + '%'
AND Gender LIKE '%' + #{userInfo.gender} + '%'
) AS t WHERE rownum BETWEEN (#{page} - 1) * #{limit} + 1 AND #{page} * #{limit}
